module.exports = {
    name: 'voiceStateUpdate',
    description: 'voiceStateUpdate event',
    run: async (client, oldState, newState) => {
        const Guild = client.guilds.cache.get("666312151354572801");
        let oldChannel = oldState.channelID;
        let newChannel = newState.channelID;
        let member = newState.member;
        let categoryID = config.tempChannelsCategoryID;
        //
        let materie = ["storia", "tpsi-sistemi", "italiano", "informatica", "gpoi", "inglese", "matematica", "ginnastica", "pcto"]
        let books = ['ðŸ“•', 'ðŸ““', 'ðŸ“”', 'ðŸ“˜', 'ðŸ“™', 'ðŸ“’', 'ðŸ“—']
        //
        let channelName = emojis[Math.floor(Math.random() * emojis.length)] + randomWords(3).join(" ") + emojis[Math.floor(Math.random() * emojis.length)] + '#';
        let permissions = [
            {
                id: Guild.roles.everyone,
                allow: [Permissions.FLAGS.VIEW_CHANNEL],
            },
        ]
        if (newChannel === config.createChannelID) {
            subjectID.forEach(async (id) => {
                if (member.roles.cache.has(id)) {
                    channelName = `Aula di ${materie[subjectID.indexOf(id)]} ${books[Math.floor(Math.random() * books.length)]} #`
                    categoryID = config.biblioChannelsCategoryID
                    permissions = [
                        {
                            id: Guild.roles.everyone,
                            allow: [Permissions.FLAGS.VIEW_CHANNEL],
                            deny: [Permissions.FLAGS.CONNECT],
                        },
                        {
                            id: id,
                            allow: [Permissions.FLAGS.VIEW_CHANNEL],
                            allow: [Permissions.FLAGS.CONNECT],
                        },

                    ]
                }
            });
            channels = Guild.channels
            channels
                .create(channelName, {
                    type: 'voice',
                    parent: categoryID,
                    permissionOverwrites: permissions
                })
                .then(channel => {

                    /*if (categoryID == config.biblioChannelsCategoryID) {
                        channels.permissionOverwrites.create(
                            channel.guild.roles.everyone, { VIEW_CHANNEL: false });
                    }*/

                    member.voice.setChannel(channel);

                    let logVoice = {
                        title: 'Canale Vocale',
                        description: `${member} ha creato => ${channelName}`,
                        color: '#18f02e'
                    }

                    client.channels.cache.get(config.voiceLogChannelID).send({ embed: logVoice });

                })
                .catch(err => {
                    console.log(('I was enable to create a temp channel!').red);
                    console.log(err);
                });
        }
        try {
            if (oldState.channel.name.endsWith('#')) {
                let role = false;
                let member = oldState.member;
                subjectID.forEach(async (id) => {
                    if (member.roles.cache.has(id)) {
                        await member.roles.remove(id)
                        role = true;
                    }
                });

                if (!role && oldState.channel.members.size < 1) {
                    oldState.channel.delete().catch(console.error)
                        .then(() => {
                            materie.forEach(materia => {
                                if (oldState.member.roles.cache.some(role => role.name === materia)) {
                                    member.roles.remove(Guild.roles.cache.find(role => role.name === materia));
                                }
                            })
                        })

                    let logVoice = {
                        title: 'Cancellato canale vocale',
                        description: `${member} => ${oldState.channel.name}`,
                        color: '#f01818'
                    }

                    client.channels.cache.get(config.voiceLogChannelID).send({ embed: logVoice });
                }
            }
        } catch (err) {
            //  console.log(('oldState does not exists!').red);

        }
    }
}
